script:
frappe.ui.form.on("Purchase Receipt", "on_submit", function(frm, doctype, name) {
	if(frm.doc.po != ""){
frappe.call({
	"method": "carapace.carapace.doctype.gate_entry.gate_entry.UpdateGE",
	args: {
		doctype: "Purchase Receipt",
		po: frm.doc.po,
		gate_entry: frm.doc.gate_entry
     	},

	callback:function(r){
     ;}
});
}
});

frappe.ui.form.on("Purchase Receipt", "onload", function(frm){
    cur_frm.set_query("gate_entry", function(){
        return {
            "filters": [
                ["Gate Entry", "gate_entry_type", "!=", "Rejection By ERP"],
                ["Gate Entry", "status", "=", "Open"]
            ]
        }
    });
});

frappe.ui.form.on("Purchase Receipt", "onload", function(frm){
    cur_frm.set_query("po", function(){
        return {
            "filters": [
                ["Purchase Order", "gate_entry", "=", frm.doc.gate_entry]
            ]
        }
    });
});

frappe.ui.form.on("Purchase Receipt", {
    "get_data": function(frm) {
		if(frm.doc.po == ""){
        		frappe.msgprint("Gate Entry : " + frm.doc.gate_entry + " Does Not Have Any Linked Purchase Order")
		}
		else{
			frappe.model.with_doc("Gate Entry", frm.doc.gate_entry, function() {
		        cur_frm.clear_table("items");
            		var tabletransfer= frappe.model.get_doc("Gate Entry", frm.doc.gate_entry)
            		$.each(tabletransfer.gate_entry_items, function(index, row){
                		var d = frm.add_child("items");
                		d.item_code = row.item_code;
				d.item_name = row.item_name;
				d.description = row.description;
				d.received_qty = row.received_qty;
				d.uom = row.uom;
			frm.refresh_field("items");
            });
        });
}
}
});

cur_frm.add_fetch('gate_entry', 'supplier_name', 'supplier')
cur_frm.add_fetch('gate_entry', 'po_no', 'po')
cur_frm.add_fetch('gate_entry', 'type_of_vehicle', 'mode_of_transport')
cur_frm.add_fetch('gate_entry', 'contact_number', 'contact_number')
cur_frm.add_fetch('gate_entry', 'driver_name', 'driver_name')
cur_frm.add_fetch('gate_entry', 'vehicle_number', 'lr_no')
cur_frm.add_fetch('gate_entry', 'remarks', 'gate_entry_remarks')
cur_frm.add_fetch('gate_entry', 'bill_or_challan', 'bill_or_challan')
cur_frm.add_fetch('gate_entry', 'bill_no', 'bill_no')
cur_frm.add_fetch('gate_entry', 'challan_no', 'challan_no')
cur_frm.add_fetch('gate_entry', 'bill_date', 'bill_date')
cur_frm.add_fetch('gate_entry', 'challan_date', 'challan_date')

frappe.ui.form.on('Purchase Order', {
	"after_save": function(frm) {
        	var grand_total = frm.doc.grand_total;
        	   frm.set_value("advice_outstanding_amount",grand_total)
		   frm.set_value("committed_amount",grand_total)
        }
});

frappe.ui.form.on('Purchase Order', {
	"order_type": function(frm) {
        	if(frm.doc.order_type == 'Service'){
        	   frm.set_value("naming_series",'SER-')
		   frm.set_value("item_group",'Services')
		}
		if(frm.doc.order_type == 'Work order'){
        	   frm.set_value("naming_series",'PO-')
		}
        }
});

frappe.ui.form.on("Purchase Order", "order_type", function(frm){
cur_frm.clear_table("items");
frm.refresh_field("items");
if(frm.doc.order_type == 'Service'){
	cur_frm.fields_dict['items'].grid.get_field('item_code').get_query = function(doc) {
        return {
            filters: [[
                'Item', 'item_group', '=', doc.item_group
            	     ]]
           	}
    	};
   }
if(frm.doc.order_type == 'Work order'){
	cur_frm.fields_dict['items'].grid.get_field('item_code').get_query = function(doc) {
        return {
            filters: [[
                'Item', 'item_group', "in",'Consumable, Products, PROJECT-Quotation, Raw Material,safety,Sub Assemblies'
            	     ]]
           	}
    	};
   }
});

frappe.ui.form.on("Purchase Order", "onload", function(frm) {
    cur_frm.set_query("budget_head", function() {
        return {
            "filters": {
                "project": frm.doc.project
            }
        };
    });
});

frappe.ui.form.on("Payment Entry", {
  payment_advice: function(frm) {
    if (frm.doc.payment_advice && frm.doc.party_type == 'Supplier'){
	cur_frm.refresh();
	cur_frm.clear_table("references");
	cur_frm.refresh_fields();

    frappe.call({
    "method": "carapace.carapace.doctype.payment_advice_form.payment_advice_form.insert_data",
args: {
doctype: "Payment Advice Form",
payment_advice: frm.doc.payment_advice
},
callback:function(r){
	var len=r.message.length;
	console.log(r)
	for (var i=0;i<len;i++){
	        var row = frm.add_child("references");
	    row.reference_doctype = r.message[i][0];
		row.reference_name = r.message[i][1];
		row.total_amount = r.message[i][2];
		row.outstanding_amount = r.message[i][3];
		row.allocated_amount = r.message[i][4];
	}
		cur_frm.refresh();
	}
    });
}
}
});

cur_frm.add_fetch("payment_advice","allocate_amount","paid_amount")
cur_frm.add_fetch("payment_advice","budget_head","budget_head")
cur_frm.add_fetch("payment_advice","reference_type","voucher_type")
cur_frm.add_fetch("payment_advice","reference_no","voucher_no")
cur_frm.add_fetch("payment_advice","total","total")

frappe.ui.form.on("Payment Entry", "on_submit", function(frm, doctype, name) {
	if(frm.doc.payment_advice){
frappe.call({
	"method": "carapace.carapace.doctype.payment_advice_form.payment_advice_form.UpdatePA",
	args: {
		doctype: "Payment Advice Form",
		payment_advice: frm.doc.payment_advice
     	},

	callback:function(r){
     }
});
}
});

frappe.ui.form.on("Payment Entry", "onload", function(frm) {
    cur_frm.set_query("payment_advice", function() {
        return {
            "filters": {
                "status": "Open",
                "party": frm.doc.party
            }
        };
    });
});

frappe.ui.form.on('Payment Entry', 'party_type', function(frm) {
   frm.set_value('party', '')
   frm.set_value('party_name', '')
   frm.set_value('paid_amount', '')
   frm.set_value('payment_advice', '')
});

frappe.ui.form.on("Payment Entry", {
    "get_data": function(frm) {
		if(frm.doc.voucher_type == 'Purchase Invoice'){
			frappe.model.with_doc(frm.doc.voucher_type, frm.doc.voucher_no, function() {
		        cur_frm.clear_table("budget_table");
            		var tabletransfer= frappe.model.get_doc(frm.doc.voucher_type, frm.doc.voucher_no)
            		$.each(tabletransfer.items, function(index, row){
                		var d = frm.add_child("budget_table");
                		d.project = row.project;
				d.budget_head = row.budget_head;
				d.amount = row.amount;
			frm.refresh_field("budget_table");
            });
});
}
		if(frm.doc.voucher_type == 'Journal Entry'){
			frappe.model.with_doc(frm.doc.voucher_type, frm.doc.voucher_no, function() {
		        cur_frm.clear_table("budget_table");
            		var tabletransfer= frappe.model.get_doc(frm.doc.voucher_type, frm.doc.voucher_no)
            		$.each(tabletransfer.accounts, function(index, row){
			if(row.debit_in_account_currency){
                		var d = frm.add_child("budget_table");
                		d.project = row.project;
				d.budget_head = row.budget_head;
				d.amount = row.debit_in_account_currency;
			frm.refresh_field("budget_table");
		}
            });
});
}
		if(frm.doc.voucher_type == 'Salary Slip'){
			frappe.model.with_doc(frm.doc.voucher_type, frm.doc.voucher_no, function() {
		        cur_frm.clear_table("budget_table");
            		var tabletransfer= frappe.model.get_doc(frm.doc.voucher_type, frm.doc.voucher_no)
            		$.each(tabletransfer.project_and_budget_allocation, function(index, row){
                		var d = frm.add_child("budget_table");
                		d.project = row.project;
				d.budget_head = row.budget_head;
				d.amount = row.amount;
			frm.refresh_field("budget_table");
            });
});
}
}
});



frappe.ui.form.on('Payment Entry', {
    refresh: function(frm) {
        if (frm.doc.voucher_type == 'Purchase Invoice') {
        	frm.set_df_property('voucher_type', 'read_only',1);
		frm.set_df_property('voucher_type', 'hidden',1);
    } 
    if (frm.doc.voucher_type != 'Purchase Invoice') {
        	frm.set_df_property('voucher_type', 'read_only',0);
		frm.set_df_property('voucher_type', 'hidden',0);
    } 
} 
});

frappe.ui.form.on('Purchase Invoice', {
	"validate": function(frm) {
        	var outstanding_amount = frm.doc.outstanding_amount;
		if(frm.doc.update_budget_head && frm.doc.committed_amount == 0){
			frm.set_value("committed_amount",outstanding_amount)
		}
        	   frm.set_value("advice_outstanding_amount",outstanding_amount)
		if (frm.doc.supplier_invoice_amount != frm.doc.total) {
        	msgprint('Supplier invoice amount : '+frm.doc.supplier_invoice_amount+ ' is not matching with basic total : '+frm.doc.total);
        	validated = false;
    } 
        }
});

frappe.ui.form.on("Purchase Invoice", "onload", function(frm) {
    cur_frm.set_query("budget_head", function() {
        return {
            "filters": {
                "project": frm.doc.project
            }
        };
    });
});

frappe.ui.form.on('Purchase Invoice', {
    update_budget_head: function(frm) {
        if(frm.doc.update_budget_head){
        msgprint("Update Commited Check Box = 1, That will Add Commited Amount in Budget Head. Uncheck this if you don't want to affect commited amount in budget head")
        }
    } 
});

cur_frm.set_query("budget_head", "items", function(doc, cdt, cdn) {
	var d = locals[cdt][cdn];
	return{
		filters: [
			['Budget Head', 'project', '=', d.project]
		]
	}
});

cur_frm.set_query("budget_head", "expenses", function(doc, cdt, cdn) {
	var d = locals[cdt][cdn];
	return{
		filters: [
			['Budget Head', 'project', '=', cur_frm.doc.project]
		]
	}
});

frappe.ui.form.on('Journal Entry Account', 'debit_in_account_currency', function(frm) {
        msgprint('Budget head required against debit amount');
});

cur_frm.set_query("budget_head", "project_and_budget_allocation", function(doc, cdt, cdn) {
	var d = locals[cdt][cdn];
	return{
		filters: [
			['Budget Head', 'project', '=', d.project]
		]
	}
});

frappe.ui.form.on('Employee', {
    validate: function(frm) {
	var d = frm.doc.project_and_budget_allocation;
        var total = 0.0
	for (var j in d){
		total += d[j].percent
	} 
	if(total != 100){
		frappe.throw("Allocation should not be more than 100 in Budget Table, Current Value is " + total)
	}
    } 
});

frappe.ui.form.on("Salary Slip", {
    "get_details": function(frm) {
	if (frm.doc.employee){
        	frappe.model.with_doc("Employee", frm.doc.employee, function() {
		cur_frm.clear_table("project_and_budget_allocation");
            		var tabletransfer= frappe.model.get_doc("Employee", frm.doc.employee)
			var total = 0.0
            		$.each(tabletransfer.project_and_budget_allocation, function(index, row){
                		var d = frm.add_child("project_and_budget_allocation");
                		d.project = row.project;
				d.budget_head = row.budget_head;
				d.percent = row.percent;
				d.amount = (frm.doc.gross_pay * (row.percent/100));
				total += d.amount 
				frm.set_value("budget_total",total)
                	frm.refresh_field("project_and_budget_allocation");
            });
        });
    }
}
});

frappe.ui.form.on('Salary Slip', {
    validate: function(frm) {
	var d = frm.doc.project_and_budget_allocation;
        var total = 0.0
	for (var j in d){
		total += d[j].percent
	} 
	if(total != 100){
		frappe.throw("Allocation should be equal to 100 in Budget Table, Current Value is " + total)
	}
    } 
});

frappe.ui.form.on("Project And Budget Allocation", {
	"percent": function(frm, cdt, cdn) {
		cur_frm.refresh();
		cur_frm.refresh_fields();
		var d = locals[cdt][cdn];
		frappe.model.set_value(d.doctype, d.name, "amount", (d.percent / 100) * frm.doc.gross_pay);
		var total = 0;
		var budget = frm.doc.project_and_budget_allocation;

   	for(var i in budget) {
		total = total + budget[i].amount;
		frm.set_value("budget_total", total);
	}	
	}
});

frappe.ui.form.on("Project And Budget Allocation", {
	"project_and_budget_allocation_remove": function(frm, cdt, cdn) {
		cur_frm.refresh();
		cur_frm.refresh_fields();
		var total = 0;
		var budget = frm.doc.project_and_budget_allocation;

   	for(var i in budget) {
		total = total + budget[i].amount;
		frm.set_value("budget_total", total);
	}	
	}
});

cur_frm.set_query("budget_head", "project_and_budget_allocation", function(doc, cdt, cdn) {
	var d = locals[cdt][cdn];
	return{
		filters: [
			['Budget Head', 'project', '=', d.project]
		]
	}
});
